import React, {Component} from 'react';
import styled from 'styled-components';

class Header extends Component{
    render(){
        return(
            <Container>
                <Element>
                    <LogoWrapper>
                        <Logo>
                            Khusinsa
                        </Logo>
                    </LogoWrapper>
                    <HeaderMenu>
                        <Menu>
                            <Nav>
                                <NavList>
                                    <NavItem>상품 검색</NavItem>
                                    <NavItem>주문 내역</NavItem>
                                    <NavItem>회원 정보</NavItem>
                                </NavList>
                            </Nav>
                            <Sign>
                                <SignItem>회원가입/로그인</SignItem>
                            </Sign>
                        </Menu>
                    </HeaderMenu>
                </Element>
            </Container>
        )
    }
}


export default Header;


const Container = styled.div`
    width: 100%;
    padding-bottom: 1px;
    border-bottom: 4px solid black;
    box-shadow: 5px 5px 5px 10px;


`
const Element = styled.div`
    display: flex;
    padding-left : 15px;
    padding-right : 15px;
    padding-top : 10px;
    padding-bottom: 10px;
    background: linear-gradient( #151515, black 20% )
`
const LogoWrapper = styled.div`
    padding-left : 20px;
    width: 20%;
    display: flex;
`
const Logo = styled.div`
    font-size : 25px;
    font-weight: 800;
    color: white;
`
const HeaderMenu = styled.div`
    width: 80%;
    order: 2;
    display:flex;
    color: white;
`
const Menu = styled.div`
    display:flex;
    position:absolute;
    top : 12px;
    right: 10px;
`
const Nav = styled.div`
    order: 2;
`
const NavList = styled.ul`
    margin: 0 auto;
    display: flex;
`
const NavItem = styled.li`
    padding-right: 20px;
    padding-left: 20px;
    display: flex;
`
const Sign = styled.div`
    text-align: center;
    padding-left: 20px;
    order:3;
`
const SignItem = styled.div`
    padding-left: 10px;
    padding-right: 10px;
    font-weight: bold;
`
